import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./singlePost.css";
import axios from "axios";

export default function SinglePost() {
  const location = useLocation();
  // console.log(location.pathname.split("/")[2]);
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({});
  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/" + path);
      setPost(res.data);
    };
    getPost();
  }, [path]);

  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
      {post.photo && (
        <img
          src={post.photo}
          alt=""
          className="singlePostImg"
        />
      )}

        <h1 className="singlePostTitle">
          Lorem ipsum dolor sit amet.
          <div className="singlePostEdit">
            <i className="singlePostIcon far fa-edit"></i>
            <i className="singlePostIcon far fa-trash-alt"></i>
          </div>
        </h1>
        <div className="singlePostInfo">
          <span className="singlePostAuthor">
            Author: <b>Safak</b>
          </span>
          <span className="singlePostDate">1 hour ago</span>
        </div>
        <p className="singlePostDesc">
          Lorem ipsum dolor, sit amet consectetur adipisicing elit. Facere sunt
          commodi esse inventore enim ducimus, perspiciatis iste est sequi
          pariatur eaque repellendus, saepe, laboriosam harum natus impedit
          officiis explicabo? Vero, qui voluptate corrupti nobis placeat ab ex
          quasi aperiam aut accusamus, facilis cum ullam minus ipsum ad incidunt
          praesentium, doloribus perferendis earum dolor provident dicta. Rem
          obcaecati cupiditate esse perspiciatis quia beatae praesentium quaerat
          ea perferendis nam cum consequatur, totam molestias, id nobis.
          Repellendus voluptates fuga minus? Dolores magnam atque ipsam a sequi!
          Unde, blanditiis? Corrupti nulla ex soluta quam.
        </p>
      </div>
    </div>
  );
}
